	public static void genImportScript(){
		File file = new File("F:\\tcbslog\\data\\qdb");
		File[] files = file.listFiles();
		StringBuffer buf = new StringBuffer();
		for(int i = 0 ; i < files.length ; i++){
			if(files[i].isFile()){
				String tmpname = files[i].getName();
				buf.append("import from tcbsusra\\");
				buf.append(tmpname);
				buf.append(" of del replace into ");
				buf.append(tmpname.replace(".del", ""));
				buf.append(";\r\n");
			}
		}
		
		System.out.println(buf);
	}

